var gulp=require("gulp"),uglify=require("gulp-uglify"),cssnano=require("gulp-cssnano"),htmlmin=require("gulp-htmlmin");gulp.task("script",function(){gulp.src(["./*.js"]).pipe(uglify()).pipe(gulp.dest("./dist"))}),gulp.task("style",function(){gulp.src(["./*.css"]).pipe(cssnano()).pipe(gulp.dest("./dist"))}),gulp.task("htm",function(){gulp.src(["./*.htm"]).pipe(htmlmin({collapseWhitespace:!0})).pipe(gulp.dest("./dist"))});